$().ready(function() {

	var $inputForm = $("#inputForm");
	$inputForm.find(":submit").prop("disabled", false);
	$inputForm.validate({
		rules: {
			name: {
				required: true,
				remote: {
					url: "/SHS/admin/ad_position/check_name",
					data: {"previousName": previousName},
					type: "post",
					cache: false
				}
			},
			ident: {
				required: true,
				pattern: /^\w+$/,
				remote: {
					url: "/SHS/admin/ad_position/check_ident",
					data: {"previousIdent": previousIdent},
					type: "post",
					cache: false
				}
			},
			width: {
				required: true,
				min: 0
			},
			height: {
				required: true,
				min: 0
			},
			template: "required"
		},
		messages: {
			name: {
				remote: "已存在"
			},
			ident: {
				pattern: "非法字符",
				remote: "已存在"
			}
		}
	});
	
});