$().ready(function() {
			
	var $inputForm = $("#inputForm");
	$inputForm.find(":submit").prop("disabled", false);
	$inputForm.validate({
		rules: {
			name: {
				required: true,
				remote: {
					url: "/SHS/admin/bank/check_name",
					type: "post",
					cache: false
				}
			},
			code: {
				required: true,
				pattern: /^\w+$/,
				remote: {
					url: "/SHS/admin/bank/check_code",
					type: "post",
					cache: false
				}
			},
			logoFile: {
				extension: "jpg,jpeg,bmp,gif,png"
			},
			order: "digits"
		},
		messages: {
			name: {
				remote: "已存在"
			},
			code: {
				pattern: "非法字符",
				remote: "已存在"
			}
		}
	});
	
});