$().ready(function() {
    
	var $inputForm = $("#inputForm");
	var $bank = $("#bank");
	$inputForm.find(":submit").prop("disabled", false);
	var $validate = $inputForm.validate({
		rules: {
			bankId: "required",
			card: {
				required: true,
				digits: true,
				remote: {
					url: "/P2P-2.0-YuXinChuangTou/admin/bank_card_modif/check_card",
					data: {previousCard: previousCard},
					type: "post",
					cache: false
				}
			},
			opinion: "required"
		},
		messages: {
			card: {
				remote: "已存在"
			}
		}
	});
	
	$("#bank").selectBind({
		url: "/P2P-2.0-YuXinChuangTou/admin/bank_branch/jsons",
		choose: "-",
		target: $("#branch")
	});
	
	$("input.selectSascade.area").selectSascade({
		url: "/P2P-2.0-YuXinChuangTou/admin/area/jsons",
		choose: "-",
		changed: true
	});
	
	$bank.change(function(){
		$validate.element(this);
	});
	
});