$().ready(function() {
    
	var $inputForm = $("#inputForm");
	var $locality = $("#locality");
	$inputForm.find(":submit").prop("disabled", false);
	var $validate = $inputForm.validate({
		rules: {
			name: {
				required: true,
				remote: {
					url: "/SHS/admin/bank_branch/check_name",
					data: {
						"bankId": typeof(bankId) == "undefined" ? "" : bankId,
						"previousName": previousName
					},
					type: "post",
					cache: false
				}
			},
			localityId: "required",
			order: "digits"
		},
		messages: {
			name: {
				remote: "已存在"
			}
		}
	});
	
	$("input.selectSascade.area").selectSascade({
		url: "/SHS/admin/area/jsons",
		choose: "-",
		changed: true
	});
	
	$locality.change(function(){
		$validate.element(this);
	});
	
});