$().ready(function() {

		var $inputForm = $("#inputForm");
	$inputForm.find(":submit").prop("disabled", false);
	$inputForm.validate({
		rules: {
			storageName: "required",
			host: "required",
			port: {
				required: true,
				digits: true
			},
			username: "required",
			urlPrefix: "required",
			order: "digits"
		}
	});
	
});