$().ready( function() {
		
	var $loginForm = $("#loginForm"), $submit = $loginForm.find(":submit"), $flashMessage = $("#flashMessage"), $remember = $("#remember");
	var $username = $("#username"), $password = $("#password"), $captcha = $("#captcha");
	
	$submit.prop("disabled", false);
	
	$loginForm.validate({
		errorClass: "errorMessage",
		rules: {
			username: {
				required: true,
				pattern: /^[0-9a-z_A-Z\u4e00-\u9fa5]+$/,
				minlength: 2
			},
			password: {
				required: true,
				minlength: 4
			}
				, captcha: {
					required: true
				}
		},
		messages: {
			username: {
				required: "请输入用户名",
				pattern: "用户名只允许包含中文、英文、数字、下划线",
				minlength: "用户名最小长度为2"
			},
			password: {
				required: "请输入密码",
				minlength: "密码最小长度为4"
			}
				, captcha: {
					required: "请输入验证码"
				}
		},
		submitHandler: function(form) {
			$submit.prop("disabled", true);
		
			var rsaKey = new RSAKey();
			rsaKey.setPublic(b64tohex(modulus), b64tohex(exponent));
			
			$.ajax({
				url: "/SHS/admin/login",
				data: {
					username: $username.val(),
					password: hex2b64(rsaKey.encrypt($password.val()))
						, captchaId: captchaId,
						captcha: $captcha.val()
				},
				type: "post",
				dataType: "json",
				cache: false,
				beforeSend: function(request, settings) {
					request.setRequestHeader("token", $.cookie("token"));
					$submit.text(" 登 陆 中 ... ");
				},
				success: function(message) {
					if ($remember.prop("checked")) {
						$.cookie("rememberAdmin", $username.val(), {expires: 7 * 24 * 60 * 60});
					} else {
						$.removeCookie("memberUsername");
					}
					if (message.type == "success") {
						window.location = message.successUrl;
					} else {
						$flashMessage.text(message.cont);
						modulus = message.modulus, exponent = message.exponent;
							$captcha.val("");
							$captchaImage.click();
						$submit.text(" 登 陆 ");
						$submit.prop("disabled", false);
					}
				},
				error: function() {
					$flashMessage.text("登陆失败，请刷新页面重试");
					$submit.text(" 登 陆 失 败 ");
				}
			});
		}
	});

	var remember = $.cookie("rememberAdmin");
	if(remember != null) {
		$remember.prop("checked", true);
		$username.val(remember);
		$password.focus();
	} else {
		$remember.prop("checked", false);
		$username.focus();
	}
		
		var $captchaImage = $("#captchaImage");
		$captchaImage.click(function() {
			$captchaImage.prop("src", "/SHS/admin/captcha?captchaId=" + captchaId + "&timestamp=" + (new Date()).valueOf());
		});

});