$().ready(function() {

		$.validator.addMethod("compareAmount", 
		function(value, element, param) {
			return this.optional(element) || $.trim(value) == "" || $.trim($(param).val()) == "" || parseFloat(value) >= parseFloat($(param).val());
		},
		"必须大于或等于最低额度"
	);

	var $inputForm = $("#inputForm");
	$inputForm.find(":submit").prop("disabled", false);
	$inputForm.validate({
		rules: {
			paymentName: "required",
			logoFile: {
				extension: "jpg,jpeg,bmp,gif,png"
			},
			bankName: "required",
			bankBranchName: "required",
			bankCardNo: {
				required: true,
				digits: true,
				minlength: 12,
				maxlength: 19
			},
			bankCardHolderName: "required",
			minimum: {
				positive: true,
				decimal: {
					integer: 12,
					fraction: 2
				}
			},
			maximum: {
				positive: true,
				decimal: {
					integer: 12,
					fraction: 2
				},
				compareAmount: "#minimum"
			},
			fee: {
				required: true,
				nonnegativeNumber: true,
				decimal: {
					integer: 19,
					fraction: 3
				}
			},
			handleTime: "required",
			order: "digits"
		}
	});
	
});