$().ready(function() {
    
	var $loginForm = $("#loginForm"), $flashMessage = $("#flashMessage"), $submit = $loginForm.find(":submit"), rsaKey = new RSAKey(), $remember = $("#remember");
	var $username = $("#username"), $password = $("#password"), $captcha = $("#captcha");
	$submit.prop("disabled", false);
	rsaKey.setPublic(b64tohex(modulus), b64tohex(exponent));
	
	$loginForm.validate({
		rules: {
			username: "required",
			password: "required"
				, captcha: "required"
		},
		messages: {
			username: "请输入您的用户名/手机号码/邮箱地址",
			password: "请输入密码"
				, captcha: "请输入验证码"
		},
		errorPlacement: function(error, element) {
			element.closest("li").find(".annotate").text(error.text());
		},
		unhighlight: function(element) {
			$(element).closest("li").find(".annotate").text("");
		},
		submitHandler: function(form) {
			$submit.prop("disabled", true);
			
			$.ajax({
				url: "/SHS/login",
				data: {
					username: $username.val(),
					password: hex2b64(rsaKey.encrypt($password.val()))
						, captchaId: captchaId,
						captcha: $captcha.val()
				},
				type: "post",
				dataType: "json",
				cache: false,
				beforeSend: function(request, settings) {
					request.setRequestHeader("token", $.cookie("token"));
					$submit.val(" 登 陆 中 ... ");
				},
				success: function(message) {
					if ($remember.prop("checked")) {
						$.cookie("remember", $username.val(), {expires: 7 * 24 * 60 * 60});
					} else {
						$.removeCookie("memberUsername");
					}
					if (message.type == "success") {
						window.location = redirectUrl;
					} else {
						$flashMessage.text(message.cont);
							$captcha.val("");
							$captchaImage.click();
						$submit.val(" 登 陆 ");
						$submit.prop("disabled", false);
					}
				},
				error: function() {
					$flashMessage.text("登陆失败，请刷新页面重试");
					$submit.val(" 登 陆 失 败 ");
				}
			});
		}
	});
	
	var remember = $.cookie("remember");
	if(remember != null) {
		$remember.prop("checked", true);
		$username.val(remember);
		$password.focus();
	} else {
		$remember.prop("checked", false);
		$username.focus();
	}
		
		var $captchaImage = $("#captchaImage");
		$captchaImage.click(function() {
			$captchaImage.prop("src", "/SHS/captcha?captchaId=" + captchaId + "&timestamp=" + (new Date()).valueOf());
		});

});