$().ready(function() {
	
	var $withdrawalForm = $("#withdrawalForm"), $submit = $withdrawalForm.find(":submit"), rsaKey = new RSAKey();
	var $bankCard = $("#bankCard"), $amount = $("#amount"), $password = $withdrawalForm.find("[name='password']");
	$submit.prop("disabled", false);
	rsaKey.setPublic(b64tohex(modulus), b64tohex(exponent));
	$amount.val("");
	$password.val("");
	
	var $validate = $withdrawalForm.validate({
		rules: {
			amount: {
				required: true,
				positive: true,
				decimal: {
					integer: 12,
					fraction: 2
				},
				remote: {
					url: "/SHS/account/withdrawal/check_balance",
					type: "post",
					cache: false
				}
			},
			bankCard: "required",
			password: "required"
		},
		messages: {
			amount: {
				required: "请输入提现金额",
				positive: "提现金额必须为正整数",
				decimal: "提现金额必须为正整数",
				remote: "可用余额不足"
			},
			password: "请输入支付密码"
		},
		errorPlacement: function(error, element) {
			element.closest("td").find(".annotate").text(error.text());
		},
		unhighlight: function(element) {
			$(element).closest("td").find(".annotate").text("");
		},
		submitHandler: function(form) {
			$submit.prop("disabled", true);
			$password.val(hex2b64(rsaKey.encrypt($password.val())));
			form.submit();
		}
	});

	$amount.bind("input propertychange change", function(event) {
		if (event.type != "propertychange" || event.originalEvent.propertyName == "value") {
			$calculateFee();
		}
	});
	
	var $fee = $("#fee");
	var $paymentAmout = $("#paymentAmout");
	var timeout;
	$calculateFee = function() {
		clearTimeout(timeout);
		timeout = setTimeout(function() {
			if ($validate.element($amount)) {
				$.ajax({
					url: "/SHS/account/withdrawal/calculate_fee",
					type: "POST",
					data: {amount: $amount.val()},
					dataType: "json",
					cache: false,
					success: function(data) {
						$fee.text(data.fee);
						$paymentAmout.text(data.amount);
					}
				});
			} else {
				$fee.text("0.00");
				$paymentAmout.text("0.00");
			}
		}, 500);
	};
	
	$(".bankCards").click(function() {
		$bankCard.val($(this).attr("val"));
	});

});